	public static boolean simpleNLP(String inputline, String keywords[]) {
		int i;
		int maxToken = keywords.length;
		int to, from;
		if (inputline.length() < 1)
			return false;

		Vector lexed = new Vector(); // stores the words
		// first extract every substring in inputline that has a blank on either
		// side.

		from = 0;
		to = 0;
		while (inputline.charAt(from) == ' ' && from < inputline.length())
			from++; // skip ' '
		if (from >= inputline.length())
			return false; // check for blank and empty lines
		while (to >= 0) {
			to = inputline.indexOf(' ', from);
			if (to > 0) {
				lexed.addElement(inputline.substring(from, to));
				from = to;
				while (inputline.charAt(from) == ' '
						&& from < inputline.length() - 1)
					from++;
			} else {
				lexed.addElement(inputline.substring(from));
			}
		}
		//
		// if we get here we have a vector of strings that correspond to the
		// words in the input.
		//
		// so now we look for matches in order
		boolean status = false;
		to = 0;
		for (i = 0; i < lexed.size(); i++) {
			String s = (String) lexed.elementAt(i);
			if (s.equalsIgnoreCase(keywords[to])) {
				to++;
				if (to >= keywords.length) {
					status = true;
					break;
				}
			}
		}
		return status;
	}

