    bool isOpaque = object.Alpha == 1.0f;
    bool isDrawable = object.CanDraw && object.Layer == currentLayer;
    bool isHidden = hideList.Find(object);
    
    bool isVisible = isOpaque && isDrawable && ! isHidden;
    
    if(isVisible)
    {
        // ...
    }
    

Better yet:

    
    bool IsVisible()
    {
        bool isOpaque = object.Alpha == 1.0f;
        bool isDrawable = object.CanDraw && object.Layer == currentLayer;
        bool isHidden = hideList.Find(object);

        return isOpaque && isDrawable && ! isHidden;
    }
    void Draw()
    {
         if(IsVisible)
         {
             // ...
         }
    }
    
    

